import React, {useContext} from 'react';
import { Box, Typography } from '@mui/material';
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';

import BodyPart from './BodyPart'; // Render this inside HorizontalScrollbar
import LeftArrowIcon from '../assets/icons/left-arrow.png';
import RightArrowIcon from '../assets/icons/right-arrow.png';

const LeftArrow = () => {
    const {scrollPrev} = useContext(VisibilityContext);

    return (
        <Typography onClick={() => scrollPrev()} className='right-arrow' >
            <img src={LeftArrowIcon} alt='left-arrow' />
        </Typography>
    );
};

const RightArrow = () => {
    const {scrollNext} = useContext(VisibilityContext);

    return (
        <Typography onClick={() => scrollNext()} className='left-arrow' >
            <img src={RightArrowIcon} alt='right-arrow' />
        </Typography>
    );
};

const HorizontalScrollbar = ({data, bodyPart, setBodyPart}) => {
  return (
    <ScrollMenu sx={{display: 'flex'}} LeftArrow={LeftArrow} RightArrow={RightArrow} >
        {data.map((item) => (
                <Box
                    key={item.id || item}
                    itemId={item.id || item}
                    title={item.id || item}
                    m='0 40px'
                >
                    {/* {item} Now items are listed. (5) Create a BodyPart.js component to display in cards. Render it below */}
                    <BodyPart item={item} bodyPart={bodyPart} setBodyPart={setBodyPart} />
                </Box>
            )
        )}
    </ScrollMenu>
  )
}

export default HorizontalScrollbar
